import "./Cart.css";
import { useState } from "react";


export default function Cart(props){
     let cartItemsCount =props.itemsCount
            
             // {itemName:"sneaker", itemPrice:500, itemCount:3}
       
       
       
       if(props.open){
        return(
            <div className="cartContainer">
                 <h4>Cart</h4>
                   { cartItemsCount.length < 1 ?  <p>Your cart is empty</p>  : 
                   <>
                      <ul>
                          {cartItemsCount.map((item)=>{
                            return(
                                   <li>
                                   <div className="row">
                                          <div>
                                               <img src={item.url} alt="productImage" />   
                                          </div>
                                          <div className="total_price_con">
                                                 <p>Fall Limited Edition Sneakers</p>
                                                 <p>${item.price + ".00"  + " x " + item.productsCount +"    $" +`${item.price * item.productsCount}` }</p>
                                          </div>
                                   </div>
                              </li>
                            )
                          })}
                      </ul>
                        <button></button>
                        </>     
                   }
            </div>
       )

       }
  
}
