import "./Cart.css";
import { useState } from "react";


export default function Cart(props){
       const [itemsCount,setItemsCount] = useState([
              {itemName:"sneaker", itemPrice:500, itemCount:3}
       ]);
       
       
       if(props.open){
        return(
            <div className="cartContainer">
                 <p>Cart</p>
                {
                    itemsCount.map((item)=>{
                                if(itemsCount  < 1){
                                   return 
                                }
                                else {
                                   return(
                                        <li>
                                             <span>
                                                   {item.itemName  + "  " }
                                             </span>
                                             <span>
                                                   {item.itemPrice + "  " + "x" + "  " + item.itemCount} 
                                             </span>
                                              <span>
                                                   { "  " + item.itemPrice * item.itemCount}
                                             </span>
                                        </li>
                                   )
                                }
                    })
                }
            </div>
       )

       }
  
}
