import "./Cart.css";
import { useState } from "react";


export default function Cart(props){
       const [itemsCount,setItemsCount] = useState([
              {}
       ]);
       
       
       if(props.open){
        return(
            <div className="cartContainer">
                 <p>Cart</p>
                {
                   itemsCount.map((item)=>{
                         if(itemsCount.length < 1){
                              return(
                                   <p>empty cat</p>
                              )
                         }
                         else{
                              return(
                                   <li>
                                   <span>
                                         {item.itemName  + "  " }
                                   </span>
                                   <span>
                                         {item.itemPrice + "  " + "x" + "  " + item.itemCount} 
                                   </span>
                                    <span>
                                         { "  " + item.itemPrice * item.itemCount}
                                   </span>
                              </li>
                              )
                         }
                   })
                }
            </div>
       )

       }
  
}
