import {BrowserRouter, Routes, Route } from "react-router-dom";
import PageHeader from "./Navbar/Header";
//import Button from "./ImageGallery/Gallery";
import Gallery from "./ImageGallery/Gallery"
import "../assets/styles/global.css";
import { useState } from "react";


export default function App(){
        const [cartItems, setCartItems] = useState([])
     function getCartItems(url,price,productsCount){
      let cartContent = {url:url, price:price, productsCount:productsCount};
      setCartItems([...cartItems,cartContent]);
      console.log(cartItems)
 }
      return (
             <BrowserRouter>
                     <PageHeader itemCount={cartItems}>
                             <Routes>
                                       <Route  path="/Gallery"  element={<Gallery  addtoCart={getCartItems}/>} />
                                       <Route path="/"  element={
                                            <>
                                               <div>
                                                       {cartItems}
                                               </div>
                                            </>
                                       }/>
                             </Routes>
                     </PageHeader>
             </BrowserRouter>
      )
}
