import PageHeader from "./Navbar/Header";
//import Button from "./ImageGallery/Gallery";
import Gallery from "./ImageGallery/Gallery"
import "../assets/styles/global.css";
import { useState } from "react";


export default function App(){
        const [cartItems, setCartItems] = useState([])
     function getCartItems(url,price,productsCount,productId){
            cartItems.map((products)=>{
                ///if()
            })
      let cartContent = {url:url, price:price, productsCount:productsCount, productId:productId};
      setCartItems([...cartItems,cartContent]);
      console.log(cartItems)
 }

 function deleteItem(productId){
        let elementIndex ;
        //==error to handle
        //because  the images has the same id, when the array is iterated 
        // the last 'element'  index which has the same id as any other element is assinged to elementIndex
        //solution  let user add the same product to cart only once
        //if he/she want to add the same product the second time let only the itemsCount be updated
             let newCart = cartItems.map((item,index)=>{
                    if(productId === item.productId){
                             elementIndex = index;
                             return item;
                    }
                    else{
                        return item;
                    }
             })
             
         newCart.splice(elementIndex,1)
       setCartItems(newCart)
 }
      return (
             <BrowserRouter>
                     <PageHeader itemCount={cartItems}  listCount={cartItems.length}   removeItem={deleteItem}>
                             <Routes>
                                       <Route  path="/"  element={<Gallery  addtoCart={getCartItems}/>} />
                                       
                             </Routes>
                     </PageHeader>
             </BrowserRouter>
      )
}
