import { useState } from "react";
import {v4 as uuidv4}  from   "uuid";
import "./Gallery.css";
import product1 from "../../assets/images/image-product-1.jpg";
import product2 from "../../assets/images/image-product-2.jpg";
import product3   from "../../assets/images/image-product-3.jpg";
import product4   from "../../assets/images/image-product-4.jpg"
import product1th   from "../../assets/images/image-product-1-thumbnail.jpg";
import product2th   from "../../assets/images/image-product-2-thumbnail.jpg";
import product3th   from "../../assets/images/image-product-3-thumbnail.jpg";
import product4th   from "../../assets/images/image-product-4-thumbnail.jpg";


export default function Gallery(props){
      const[relatedImage, setRelatedImage] = useState(
           [ {productName: "product1", productId:1, productUrl:product1 , producttUrl:product1th,price:125,discount:"50%"},
            {productName: "product2", productId:2, productUrl:product2,producttUrl:product2th, price:125,discount:"50%"},
            {productName: "product3", productId:3, productUrl:product3,producttUrl:product3th,price:125,discount:"50%"},
            {productName: "product4", productId:4, productUrl:product4,producttUrl:product4th,price:125,discount:"50%"}
       ]
      );
      const[activeImage, setActiveImage] = useState(relatedImage[0]);
         return(
              <section className="collection-container">
                      <div className="gallery">
                           <div className="activeImage">
                                  <img src={activeImage.productUrl} alt="" />
                           </div> 
                           <div className="related-images">                           
                              {
                                    relatedImage.map((image)=>{
                                          return(
                                          <div className="items item1" key={uuidv4()}>
                                                <img src={image.producttUrl} alt="" />
                                          </div> 
                                          )
                                    })
                             }        
                           </div>            
                      </div>
                       <div className="description-container">
                             <div className="product-description ">
                             <h4  className="fw-5  text-orange mb-1_5">SNEAKER COMPANY</h4>
                                 <h1 className="font-2_2  fw-900  line-height-2_4  mb-2">Fall Limited Edition Sneakers</h1>
                                 <p className="text-gray">These low-profile sneakers are your perfect casual wear</p>
                                 <p className="text-gray">companion. Featuring a durable ruber outer sole, they'll</p>
                                 <p className="text-gray">withstand everything the weather can offer</p>
                             </div>
                                
                              <div className="quantity mt-1">
                                      <div className="pricing">
                                      <span id="price" className="font-2 fw-900 d-inline-block ">{"$" + activeImage.price +".00"}</span>
                                      <span id="" className="d-inline-block text-center discount">{activeImage.discount}</span>
                                      </div>
                              </div>
                                 
                       </div>   
              </section>
         );
}
